import React from "react";
import classNames from 'classnames';
//nodejs library that concatenates style
import withStyles from "@material-ui/core/styles/withStyles";

//@material-ui/core components
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import ButtonBase from '@material-ui/core/ButtonBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import SwipeableViews from 'react-swipeable-views';
import Zoom from '@material-ui/core/Zoom';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import EditIcon from '@material-ui/icons/Edit';
import UpIcon from '@material-ui/icons/KeyboardArrowUp';


//core components
import tileData from "./GridList.jsx";
import AppBar from "components/appBar/AppBar.jsx"


import ImageGridListStyle  from "assets/jss/material-kit-react/components/imageGridListStyle.jsx";

class ImageGridList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value : 0
		}
	};

	handleChange = (event, value) => {
		this.setState({ value });
	};

	handleChangeIndex = index => {
		console.log("ini index" + index);
		this.setState({value : index})
	}

	render() {
		const { classes, theme } = this.props;
	    const transitionDuration = {
	      enter: theme.transitions.duration.enteringScreen,
	      exit: theme.transitions.duration.leavingScreen,
	    };

	    const fabs = [
	      {
	        color: 'primary',
	        className: classes.fab,
	        icon: <AddIcon />,
	      },
	      {
	        color: 'secondary',
	        className: classes.fab,
	        icon: <EditIcon />,
	      },
	      {
	        color: 'inherit',
	        className: classNames(classes.fab, classes.fabGreen),
	        icon: <UpIcon />,
	      },
	    ];
		return(
			<div className={classes.root}>
		        <Tabs
		          value={this.state.value}
		          onChange={this.handleChange}
		          classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
		        >
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="All Design"
		          />
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Part Website"
		          />
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Full Website"
		          />
		        </Tabs>
			        <SwipeableViews
			          axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
			          index={this.state.value}
			          onChangeIndex={this.handleChangeIndex}
			        >
			          <AppBar dir={theme.direction}>All Design</AppBar>
			          <AppBar dir={theme.direction}>Part Website</AppBar>
			          <AppBar dir={theme.direction}>Full Website</AppBar>
			        </SwipeableViews>
			        {fabs.map((fab, index) => (
			          <Zoom
			            key={fab.color}
			            in={this.state.value === index}
			            timeout={transitionDuration}
			            style={{
			              transitionDelay: `${this.state.value === index ? transitionDuration.exit : 0}ms`,
			            }}
			            unmountOnExit
			          >
			            <Fab className={fab.className} color={fab.color}>
              {fab.icon}
            </Fab>
			          </Zoom>
			        ))}
	        </div>
		);
	}
};

export default withStyles(ImageGridListStyle, { withTheme: true })(ImageGridList);
