import React from "react";
import PropTypes from 'prop-types';
//nodejs library that concatenates style
import withStyles from "@material-ui/core/styles/withStyles";

//@material-ui/core components
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import ButtonBase from '@material-ui/core/ButtonBase';
import SwipeableViews from 'react-swipeable-views';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Zoom from '@material-ui/core/Zoom';
import Typography from '@material-ui/core/Typography';

//core components
import tileData from "./GridList.jsx";


import ImageGridListStyle  from "assets/jss/material-kit-react/components/imageGridListStyle.jsx";

function TabContainer({ children, dir }) {
  return (
    <Typography component="div" dir={dir} >
      {children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
  dir: PropTypes.string.isRequired,
};

class ImageGridList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			index : 0,
			checked: true
		}
	};

	handleChange = (event, value) => {
		this.setState({ 
			index : value,
			checked: true
		});
	};
	handleChangeIndex = index => {
    	this.setState({ 
    		value: index,
    		checked: true 
    	});
  	};

	render() {
		const { classes, theme } = this.props;
	    const transitionDuration = {
	      enter: theme.transitions.duration.enteringScreen,
	      exit: theme.transitions.duration.leavingScreen,
	    };
		return(
			<div className={classes.root}>
		        <Tabs
		          value={this.state.index}
		          onChange={this.handleChange}
		          classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
		        >
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="All Design"
		          >
		          </Tab>
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Part Website"
		          />
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Full Website"
		          />
		        </Tabs>
		        <SwipeableViews
          		index={this.state.value}
          		onChangeIndex={this.handleChangeIndex} 
		        enableMouseEvents
		        >
	          	{this.state.index === 0 &&
	          	<TabContainer dir={theme.direction}>
				<div className={classes.imageGrid}>
		       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
						{tileData.filter(tiles => {
							return tiles.img
						}).map( (tile,index) => (
							<GridListTile key={tile.id} cols={tile.cols} rows={tile.cols}>
								<Zoom
									in={this.state.checked}
									timeout={transitionDuration}
									style={{
									  transitionDelay: `${this.state.index  ? transitionDuration.exit : 0}ms`,
									}}
								>
								<ButtonBase
								  focusRipple
								  className={classes.image}
								  focusVisibleClassName={classes.focusVisible}
								  style={{
								    position: "unset"
								  }}
								>

									<span >
										<img src={tile.img} alt={tile.title} className={classes.gridImg} />
									</span>
									<span className={classes.imageBackdrop} />

								</ButtonBase>
								</Zoom>
							</GridListTile>
						   ))
						}
					</GridList>
				</div>
				</TabContainer>
				}
				{this.state.index === 1 &&
				<TabContainer dir={theme.direction}>
				<div className={classes.imageGrid}>
		       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
						{tileData.filter(tiles => {
							return tiles.enable === 0;
						}).map( (tile,index) => (
							<GridListTile key={tile.id} cols={tile.cols} rows={tile.cols}>
								<Zoom
									in={this.state.checked}
									timeout={transitionDuration}
									style={{
									  transitionDelay: `${this.state.index ? transitionDuration.exit : 0}ms`,
									}}
								>
								<ButtonBase
								  focusRipple
								  className={classes.image}
								  focusVisibleClassName={classes.focusVisible}
								  style={{
								    position: "unset"
								  }}
								>

									<span >
										<img src={tile.img} alt={tile.title} className={classes.gridImg} />
									</span>
									<span className={classes.imageBackdrop} />

								</ButtonBase>
								</Zoom>
							</GridListTile>
						   ))
						}
					</GridList>
				</div>
				</TabContainer>
				}
				{this.state.index === 2 &&
				<TabContainer dir={theme.direction}>
				<div className={classes.imageGrid}>
		       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
						{tileData.filter(tiles => {
							return tiles.enable === 1;
						}).map( (tile,index) => (
							<GridListTile key={tile.id} cols={tile.cols} rows={tile.cols}>
								<Zoom
									in={this.state.checked}
									timeout={transitionDuration}
									style={{
									  transitionDelay: `${this.state.index  ? transitionDuration.exit : 0}ms`,
									}}
								>
								<ButtonBase
								  focusRipple
								  className={classes.image}
								  focusVisibleClassName={classes.focusVisible}
								  style={{
								    position: "unset"
								  }}
								>

									<span >
										<img src={tile.img} alt={tile.title} className={classes.gridImg} />
									</span>
									<span className={classes.imageBackdrop} />

								</ButtonBase>
								</Zoom>
							</GridListTile>
						   ))
						}
					</GridList>
				</div>
				</TabContainer>
				}
			</SwipeableViews>
	        </div>
		);
	}
};

export default withStyles(ImageGridListStyle, { withTheme: true })(ImageGridList);
