import React from "react";
//nodejs library that concatenates style
import withStyles from "@material-ui/core/styles/withStyles";

//@material-ui/core components
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';


//core components
import GridSection from "./sections/GridSection.jsx";
import tileData from "./sections/ListImage.jsx";


import ImageGridListStyle  from "assets/jss/material-kit-react/components/imageGridListStyle.jsx";

class ImageGridList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			index : 0
		}
	};

	handleChange = (event, index) => {
		console.log("this is handlechange" + index);
		this.setState({ 
			index
		});
	};

	handleChangeIndex = value => {
		this.setState({
		  index : value
		});
	};

	selectImage = index => {
		switch(index) {
			case 0:
    			return <GridSection indexIn={{index}} imageURLs={tileData.filter(tiles => tiles.img)}/>;
    		case 1:
    			return <GridSection indexIn={{index}} imageURLs={tileData.filter(tiles => tiles.enable === 0)}/>;
			case 2:
				return <GridSection indexIn={{index}} imageURLs={tileData.filter(tiles => tiles.enable === 1)}/>;
			default:
				return <h1>you got an error</h1>
		}
	}

	render() {
		const { classes } = this.props;
		const { index } = this.state;

		return(
			<div className={classes.root}>
		        <Tabs
		          value={index}
		          onChange={this.handleChange}
		          classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
		        >
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="All Design"
		          >
		          </Tab>
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Part Website"
		          />
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Full Website"
		          />
		        </Tabs>
		        {this.selectImage}
	        </div>
		);
	}
};

export default withStyles(ImageGridListStyle, { withTheme: true })(ImageGridList);
