import React from "react";
import PropTypes from 'prop-types';
//nodejs library that concatenates style
import withStyles from "@material-ui/core/styles/withStyles";

//@material-ui/core components
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';


//core components
import GridSection from "./sections/GridSection.jsx";
import tileData from "./sections/ListImage.jsx";


import ImageGridListStyle  from "assets/jss/material-kit-react/components/imageGridListStyle.jsx";

const ImageGridList = ({...props }) => {
	const { classes, indexed, handleChange } = props;
	let image;
	switch(indexed) {
		case 0:
			image = <GridSection indexIn={{indexed}} imageURLs={tileData.filter(tiles => tiles.img)}/>;
			break;
		case 1:
			image = <GridSection indexIn={{indexed}} imageURLs={tileData.filter(tiles => tiles.enable === 0)}/>;
			break;
		case 2:
			image = <GridSection indexIn={{indexed}} imageURLs={tileData.filter(tiles => tiles.enable === 1)}/>;
			break;
		default:
			image = <h1>you got an error</h1>
			break;
	}
	return(
		<div className={classes.root}>
	        <Tabs
	          value={indexed}
	          onChange={handleChange}
	          classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
	        >
	          <Tab
	            disableRipple
	            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
	            label="All Design"
	          >
	          </Tab>
	          <Tab
	            disableRipple
	            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
	            label="Part Website"
	          />
	          <Tab
	            disableRipple
	            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
	            label="Full Website"
	          />
	        </Tabs>
	        {image}
        </div>
	);
};

ImageGridList.propTypes = {
	classes: PropTypes.object.isRequired,
	indexed: PropTypes.object.isRequired,
	handleChange : PropTypes.func.isRequired
}

export default withStyles(ImageGridListStyle)(ImageGridList);
