import { defaultFont } from "assets/jss/material-kit-react.jsx";

const headerLinkStyle = theme => ({
	list: {
		...defaultFont,
		fontSize: "14px",
		margin: 0,
		paddingLeft: "0",
		listStyle: "none",
		paddingTop: "0",
		paddingBottom: "0",
		color: "inherit"
	},
	listItem: {
		float: "left",
		color: "inherit",
		position: "relative",
		display: "block",
		width: "auto",
		margin: "0",
		padding: "0",
		[theme.breakpoints.down("sm")]: {
	      width: "100%",
	      "&:after": {
	        width: "calc(100% - 30px)",
	        content: '""',
	        display: "block",
	        height: "1px",
	        marginLeft: "15px",
	        backgroundColor: "#e5e5e5"
	      }
	    }
	},
	listItemText: {
		padding: "0 !important"
	},
	navLink: {
		color: "inherit",
		position: "relative",
		padding: "0.9375rem",
		fontWeight: "400",
		fontSize: "12px",
		textTransform: "uppercase",
		borderRadius: "3px",
		lineHeight: "20px",
		textDecoration: "none",
		margin: "0px",
		display: "inline-flex",
		"&:hover, &:focus": {
			color: "blue",
			background: "black"
		},
		[theme.breakpoints.down("sm")]: {
	      width: "calc(100% - 30px)",
	      marginLeft: "15px",
	      marginBottom: "8px",
	      marginTop: "8px",
	      textAlign: "left",
	      "& > span:first-child": {
	        justifyContent: "flex-start"
	      }
	    }
	},
	icons: {
		width: "20px",
		height: "20px",
		marginRight: "3px"
	},
})

export default headerLinkStyle;
