import React from "react";
import PropTypes from 'prop-types';
//nodejs library that concatenates style
import withStyles from "@material-ui/core/styles/withStyles";

//@material-ui/core components
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import ButtonBase from '@material-ui/core/ButtonBase';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import SwipeableViews from 'react-swipeable-views';
import Zoom from '@material-ui/core/Zoom';


//core components
import tileData from "./GridList.jsx";
import TabContainer from "components/tab/TabContainer.jsx";


import ImageGridListStyle  from "assets/jss/material-kit-react/components/imageGridListStyle.jsx";

class ImageGridList extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			index : 0
		}
	};

	handleChange = (event, value) => {
		this.setState({ index : value });
	};

	handleChangeIndex = index => {
		this.setState({index})
	}

	render() {
		const { classes, theme } = this.props;
		return(
			<div className={classes.root}>
		        <Tabs
		          value={this.state.index}
		          onChange={this.handleChange}
		          classes={{ root: classes.tabsRoot, indicator: classes.tabsIndicator }}
		        >
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="All Design"
		          >
		          </Tab>
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Part Website"
		          />
		          <Tab
		            disableRipple
		            classes={{ root: classes.tabRoot, selected: classes.tabSelected }}
		            label="Full Website"
		          />
		        </Tabs>
			        <SwipeableViews
			          axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
			          index={this.state.index}
			          onChangeIndex={this.handleChangeIndex}
			        >
			          	{this.state.index === 0 &&
			          	<TabContainer> 
						<div className={classes.imageGrid}>
				       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
								{tileData.filter(tile => {
									return tile.img;
								}).map( tile => (
									<GridListTile cols={tile.cols} rows={tile.cols}>
										<Zoom
											in={tile.img}
											style={{ transitionDelay: this.state.index ? '500ms' : '0ms' }}
										>
										<ButtonBase
										  focusRipple
										  className={classes.image}
										  focusVisibleClassName={classes.focusVisible}
										  style={{
										    position: "unset"
										  }}
										>

											<span >
												<img src={tile.img} alt={tile.title} className={classes.gridImg} />
											</span>
											<span className={classes.imageBackdrop} />

										</ButtonBase>
										</Zoom>
									</GridListTile>
								   ))
								}
							</GridList>
						</div>
						</TabContainer>
						}
						{this.state.index === 1 &&
						<TabContainer> 
						<div className={classes.imageGrid}>
				       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
								{tileData.filter(tile => {
									return tile.enable === 0;
								}).map( tile => (
									<GridListTile cols={tile.cols} className={classes.gridList} rows={tile.cols}>
										<Zoom
											in={tile.img}
											style={{ transitionDelay: this.state.index ? '500ms' : '0ms' }}
										>
										<ButtonBase
										  focusRipple
										  className={classes.image}
										  focusVisibleClassName={classes.focusVisible}
										  style={{
										    position: "unset"
										  }}
										>

											<span >
												<img src={tile.img} alt={tile.title} className={classes.gridImg} />
											</span>
											<span className={classes.imageBackdrop} />

										</ButtonBase>
										</Zoom>
									</GridListTile>
								   ))
								}
							</GridList>
						</div>
						</TabContainer>
						}
						{this.state.index === 2 &&
						<TabContainer> 
						<div className={classes.imageGrid}>
				       		<GridList cellHeight={180} className={classes.gridList} cols={4}>
								{tileData.filter(tile => {
									return tile.enable === 1;
								}).map( tile => (
									<GridListTile key={tile.img} cols={tile.cols} rows={tile.cols}>
										<Zoom
											in={tile.img}
											style={{ transitionDelay: this.state.index ? '500ms' : '0ms' }}
										>
										<ButtonBase
										  focusRipple
										  className={classes.image}
										  focusVisibleClassName={classes.focusVisible}
										  style={{
										    position: "unset"
										  }}
										>

											<span >
												<img src={tile.img} alt={tile.title} className={classes.gridImg} />
											</span>
											<span className={classes.imageBackdrop} />

										</ButtonBase>
										</Zoom>
									</GridListTile>
								   ))
								}
							</GridList>
						</div>
						</TabContainer>
						}
			        </SwipeableViews>

	        </div>
		);
	}
};

ImageGridList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(ImageGridListStyle, { withTheme: true })(ImageGridList);
