const ImageGridListStyle = theme => ({
	root: {
	    display: 'flex',
	    flexWrap: 'wrap',
	    justifyContent: 'space-around',
	    overflow: 'hidden',
	},
	gridList: {
		width: "100%",
		height: "auto"
	},
	gridImg: {
		width: "100% !important",
		height: "100% !important"
	},
	image: {
		position: 'relative',
		height: 200,
		[theme.breakpoints.down('xs')]: {
		  width: '100% !important', // Overrides inline-style
		  height: 100,
		},
		'&:hover, &$focusVisible': {
		  zIndex: 1,
		  '& $imageBackdrop': {
		    opacity: 0.15,
		  },
		},
	},
	focusVisible: {},
		imageButton: {
		position: 'absolute',
		left: 0,
		right: 0,
		top: 0,
		bottom: 0,
		display: 'flex',
		alignItems: 'center',
		justifyContent: 'center',
		color: theme.palette.common.white,
	},
	imageSrc: {
		padding: "5px"
	},
	imageBackdrop: {
		backgroundColor: theme.palette.common.black,
		opacity: 0.4,
		transition: theme.transitions.create('opacity'),
	},
});

export default ImageGridListStyle;
