import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
//@material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";


//core/components
import GridContainer from "components/grid/GridContainer.jsx";
import GridItem from "components/grid/GridItem.jsx";
import Card from "components/card/Card.jsx";
import CardBody from "components/card/CardBody.jsx";


import aboutMeStyle from "assets/jss/material-kit-react/view/landingPageSections/aboutMeStyle.jsx";

import profile from "assets/img/profile.jpg";


class AboutMe extends React.Component {
	render() {
		const { classes } = this.props;
		const imagesClasses = classNames(
			classes.imgRaised,
			classes.imgRoundedCircle,
			classes.imgFluid
		);
		return(
			<div className={classes.section} >
				<GridContainer >
					<Card plain>
						<h3>ABOUT ME </h3>
						<CardBody>
							<p className={classes.description}>
		                    You can write here details about one of your team members.
		                    You can give more details about what they do. Feel free to
		                    add some <a href="#pablo">links</a> for people to be able to
		                    follow them outside the site.
		                  	</p>
		                  	<h4 className={classes.cardTitle}>
			                  Wahyu Aji Sulaiman
			                  <br />
			                  <small className= {classes.smallTitle} >Web Designer</small>
			                </h4>
						</CardBody>
	                  	<GridItem className={classes.itemGrid}>
							<img src={profile} alt="..." className={imagesClasses}/>
						</GridItem>
					</Card>
				</GridContainer>
			</div>
		);
	}
}

export default withStyles(aboutMeStyle)(AboutMe);
